import sysimport timeimport pygame as pgfrom menu import *from settings import *from map import *from player import *class Game:    def __init__(self):        pg.init()        self.screen = pg.display.set_mode(RES)        self.clock = pg.time.Clock()        self.new_game()    def new_game(self):        self.map = Map(self)        self.player = Player(self)        self.menu = Menu(self)    def update(self):        self.player.update()        pg.display.flip()        self.clock.tick(FPS)        pg.display.set_caption(f'Snake FPS ({self.clock.get_fps() :.1f})')    def draw(self):        self.screen.fill('black')        self.map.draw()        self.player.draw()        #self.menu.show_menu()    def check_events(self):        for event in pg.event.get():            if event.type == pg.QUIT:                pg.quit()                sys.exit()    def run(self):        while True:            time.sleep(0.1)            self.check_events()            self.update()            self.draw()if __name__ == '__main__' :    game = Game()    game.run()